import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Movie } from '../movie';
import { MovieService } from '../movie.service';

@Component({ 
  selector: 'app-movie-detail',
  templateUrl: './movie-detail.component.html',
  styleUrls: ['./movie-detail.component.css']
})

export class MovieDetailComponent implements OnInit {
  movie: Movie | undefined;
  movies: Movie[] = []:
  /*movies: Movie = { 
    id: 0, 
    title: '', 
    releaseDate: '', 
    genre: '', 
    description: '' };
*/
  constructor(private route: ActivatedRoute,
              private movieService: MovieService,) { }


  ngOnInit(): void {

    const movieId = Number(this.route.snapshot.paramMap.get('id'));
    this.movie = this.movieService.getMovieById(movieId);
  }
}

<div class="movie-detail-container" *ngIf="movie">
  <h1 appHighlight class="movie-title">{{ movie.title }}</h1>
  <div class="movie-image-container">
      <img [src]="movie.imageUrl" alt="{{ movie.title }}" class="movie-detail-image" />
  </div>
  <p><strong>Release Date:</strong> {{ movie.releaseDate }}</p>
  <p><strong>Description:</strong> {{ movie.description }}</p>
  <p><strong>Genre:</strong> {{ movie.genre }}</p>
</div>
